window.onload = function(){
  $.ajax({
    url: saying_url,
    success: function (result) {
      $("#saying").text(result);
    },
  });
}

function UtoB() {
  var str = document.getElementById("utf8_input").value;
  if (str == "") {
    alert("文本输入为空！请检查后再次输入！");
    console.warn("User input empty value into utf-8 input label!");
  } else if (str != "") {
    document.getElementById("base64_input").value = utftobase(str);
  } else {
    alert("未知错误！请刷新页面后再试！");
  }
}

function BtoU() {
  var str = document.getElementById("base64_input").value;
  if (str == "") {
    alert("文本输入为空！请检查后再次输入！");
    console.warn("User input empty value into base64 input label!");
  } else if (str != "") {
    document.getElementById("utf8_input").value = basetoutf(str);
  } else {
    alert("未知错误！请刷新页面后再试！");
  }
}

function clear_info() {
  document.getElementById("base64_input").value = "";
  console.log("Base64 label cleared!");
  document.getElementById("utf8_input").value = "";
  console.log("UTF-8 label cleared!");
}

function utftobase(str) {
  return window.btoa(unescape(encodeURIComponent(str)));
}

function basetoutf(str) {
  return decodeURIComponent(escape(window.atob(str)));
}

function copy_utf8() {
  var info = document.getElementById("utf8_input").value;
  if (info != '') {
    var text_area = document.getElementById("utf8_input");
    text_area.select();
    document.execCommand("Copy");
    new bootstrap.Toast(document.querySelector('#utf8_toast')).show();
  } else {
    alert("未发现UTF-8内容！请确认后重新点击按钮！");
  }
}

function copy_base64() {
  var info = document.getElementById("base64_input").value;
  if (info != '') {
    var text_area = document.getElementById("base64_input");
    text_area.select();
    document.execCommand("Copy");
    new bootstrap.Toast(document.querySelector('#base64_toast')).show();
  } else {
    alert("未发现Base64内容！请确认后重新点击按钮！");
  }
}