var api_url = "https://api.oick.cn/wyy/api.php?id=";

function check_id(){
  var id = document.getElementById("song-id").value;
  if (id == '') {
    alert("请输入音乐ID信息！");
    console.warn("empty ID info entered!");
  } else {
    var netease = "https://music.163.com/#/song?id="+id;
    window.open(netease,"_blank");
    console.log("User checked song by ID: "+id);
  }
}

function jump_netease(){
  window.open("https://music.163.com/#","_blank");
  console.log("User opened Netease Website!");
}

function clear_info(){
  document.getElementById("song-id").value='';
}

function download_song(){
  var id = document.getElementById("song-id").value;
  if (id == '') {
    alert("请输入音乐ID信息！");
    console.warn("empty ID info entered!");
  } else {
    var post_url = api_url+id;
    console.log("User download song by ID: "+id);
    console.warn("post url: "+post_url);
    document.getElementById("download_btn").href = post_url;
    document.getElementById("download_btn").click();
  }
}