
window.addEventListener('load',function(){
  img_onload();
});

var api_url = "https://api.oick.cn/random/api.php?type=";

var device_w = document.documentElement.clientWidth;
var device_h = document.documentElement.clientHeight;
console.log(device_w+"*"+device_h);

function img_onload() {
  if (device_w >= device_h) {
    console.log("PC image load prepared...");
    load_img("pc");
  } else if (device_w < device_h) {
    console.log("PE image load prepared...");
    load_img("pe");
  }
}

function load_img(type) {
  document.getElementById("img_show").src='';
  const post_url = api_url + type;
  document.getElementById("img_show").src=post_url;
}

function load_pc_img(){
  load_img("pc");
}

function load_pe_img(){
  load_img("pe");
}